 (11,'{\"kindIdMap\":{\"tính từ\":1,\"động từ\":0,\"adverb\":6,\"danh từ\":2,\"verb\":9,\"phó từ\":5,\"ngoại động từ\":4,\"noun\":8,\"nội động từ\":3,\"adjective\":7},\"pron\":\"/diːm/\",\"soundSource\":\"playSoundFromFlash(\\u0027http://dictionary.cambridge.org/media/british/us_pron/d/dee/deem_/deem.mp3\\u0027, this)\",\"meanings\":[{\"content\":\"deem that\",\"examples\":[{\"content\":\"They deemed that he was no longer capable of managing the business.\"}],\"type\":\"gram\"},{\"content\":\"deem something necessary/appropriate etc\",\"examples\":[{\"content\":\"They were told to take whatever action they deemed necessary.\"}],\"type\":\"collo\"},{\"content\":\"be deemed to be something\",\"examples\":[{\"content\":\"They were deemed to be illegal immigrants.\"}],\"type\":\"gram\"},{\"content\":\"be deemed to do something\",\"examples\":[{\"content\":\"UK plans were deemed to infringe EU law.\"}],\"type\":\"gram\"},{\"content\":\" to think of something in a particular way or as having a particular quality\",\"examples\":[{\"content\":\"They were deemed to be illegal immigrants.\"},{\"content\":\"UK plans were deemed to infringe EU law.\"},{\"content\":\"They were told to take whatever action they deemed necessary.\"},{\"content\":\"They deemed that he was no longer capable of managing the business.\"}]},{\"content\":\"tưởng rằng, nghĩ rằng, cho rằng, thấy rằng\",\"kindId\":4,\"examples\":[{\"content\":\"to deem it one\\u0027\\u0027s duty\"},{\"content\":\"to deem highly of...\"},{\"content\":\"I deem it necessary to help him\"}]}],\"description\":\"deem\",\"meaningMap\":{},\"timeStamp\":1295852386816}','deem','/diːm/','playSoundFromFlash(\'http://dictionary.cambridge.org/media/british/us_pron/d/dee/deem_/deem.mp3\', this)',1295852386816),
 (12,'{\"kindIdMap\":{\"tính từ\":1,\"động từ\":0,\"adverb\":6,\"danh từ\":2,\"verb\":9,\"phó từ\":5,\"ngoại động từ\":4,\"noun\":8,\"nội động từ\":3,\"adjective\":7},\"pron\":\"/ɪkˈskluː.sɪv/\",\"soundSource\":\"playSoundFromFlash(\\u0027http://dictionary.cambridge.org/media/british/us_pron/e/exc/exclu/exclusive.mp3\\u0027, this)\",\"meanings\":[{\"content\":\" if two things are mutually exclusive, you cannot have or do both of them\",\"examples\":[{\"content\":\"Lesbianism and motherhood are not mutually exclusive.\"}]},{\"content\":\"(dùng như phó từ) trừ, không kể, không gồm\",\"kindId\":1,\"examples\":[{\"content\":\"from page one to page ten exclusive\"},{\"content\":\"100,000 people exclusive of women\"}]},{\"content\":\" exclusive places, organizations, clothes etc are so expensive that not many people can afford to use or buy them\",\"examples\":[{\"content\":\"an exclusive girls\\u0027\\u0027 school\"},{\"content\":\"Bel Air is an exclusive suburb of Los Angeles.\"}]},{\"content\":\" not including something\",\"examples\":[{\"content\":\"Our prices are exclusive of sales tax.\"}]},{\"content\":\"exclusive access/rights/use etc\",\"examples\":[{\"content\":\"Our figure skating club has exclusive use of the rink on Mondays.\"}],\"type\":\"collo\"},{\"examples\":[{\"content\":\"This offer is exclusive to readers of The Sun.\"}],\"type\":\"gram\"},{\"content\":\" an important or exciting story that is printed in only one newspaper, because that newspaper was the first to find out about it\",\"examples\":[{\"content\":\"a New York Post exclusive about the Kennedy marriage\"}]},{\"content\":\" deliberately not allowing someone to do something or be part of a group\",\"examples\":[{\"content\":\"a racially exclusive hiring policy\"}]},{\"content\":\"riêng biệt, dành riêng (câu lạc bộ, cửa hàng...); độc chiếm, độc quyền\",\"kindId\":1,\"examples\":[{\"content\":\"an exclusive interview\"},{\"content\":\"exclusive right to sell something\"}]},{\"content\":\" availa