 (194,'{\"kindIdMap\":{\"tính từ\":1,\"động từ\":0,\"adverb\":6,\"danh từ\":2,\"verb\":9,\"phó từ\":5,\"ngoại động từ\":4,\"noun\":8,\"nội động từ\":3,\"adjective\":7},\"pron\":\"/ˈsæn.ɪ.ti/\",\"soundSource\":\"playSoundFromFlash(\\u0027http://dictionary.cambridge.org/media/british/us_pron/e/eus/eus74/eus74063.mp3\\u0027, this)\",\"meanings\":[{\"content\":\"regain/get back/recover your sanity\",\"examples\":[{\"content\":\"I took a vacation to try to recover my sanity.\"}],\"type\":\"collo\"},{\"content\":\" when someone or something is being reasonable and sensible\",\"examples\":[{\"content\":\"Sanity appears to be returning to the stock market.\"},{\"content\":\"I took a vacation to try to recover my sanity.\"}]},{\"content\":\"sự ôn hoà, sự đúng mực (quan điểm)\",\"kindId\":2,\"examples\":[]},{\"content\":\"sự lành mạnh (tinh thần)\",\"kindId\":2,\"examples\":[]},{\"content\":\" the condition of being mentally healthy\",\"examples\":[{\"content\":\"She wondered if she was losing her sanity.\"},{\"content\":\"I began to doubt his sanity.\"}]}],\"description\":\"sanity\",\"meaningMap\":{},\"timeStamp\":1295865521011}','sanity','/ˈsæn.ɪ.ti/','playSoundFromFlash(\'http://dictionary.cambridge.org/media/british/us_pron/e/eus/eus74/eus74063.mp3\', this)',1295865521011),
